package com.hsartori.challenges.interviews;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class VowelSubstringTest {

    @Test
    void vowelsubstring_case0() {
        final int result = VowelSubstring.vowelsubstring("aaeiouxa");
        assertEquals(2, result);
    }

    @Test
    void vowelsubstring_case1() {
        final int result = VowelSubstring.vowelsubstring("axyzaeiou");
        assertEquals(1, result);
    }

    @Test
    void vowelsubstring_case2() {
        final int result = VowelSubstring.vowelsubstring("");
        assertEquals(308, result);
    }

}
